import React, { useState } from 'react';
import './LoginPage.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleLogin = () => {
    const userCredentials = localStorage.getItem(email);
    if (!userCredentials) {
      setMessage("User not found. Please register.");
      setMessageType('error');
      return;
    }

    const userData = JSON.parse(userCredentials);
    if (userData.password === password) {
      localStorage.setItem('currentUser', email); // Store current user email in localStorage
      localStorage.setItem('isLoggedIn', 'true'); // Mark the user as logged in
      window.location.href = '/profile'; // Redirect to the profile page after successful login
    } else {
      setMessage("Incorrect password.");
      setMessageType('error');
    }
  };

  return (
    <div className="login-page">
      <h2>Log in</h2>
      {message && <div className={`message ${messageType}`}>{message}</div>}
      <div className="input-container">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}

export default LoginPage;
