import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, DirectionsRenderer, DirectionsService } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

// Задана локація клієнта
const clientLocation = {
  lat: 100.84, 
  lng: 24.03
};

function DriverMapPage() {
  const [currentLocation, setCurrentLocation] = useState(null);
  const [directionsResponse, setDirectionsResponse] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };
        setCurrentLocation(pos);
      }, () => {
        alert('Error: The Geolocation service failed.');
      });
    } else {
      // Browser doesn't support Geolocation
      alert('Error: Your browser doesn\'t support geolocation.');
    }
  }, []);

  return (
    <LoadScript googleMapsApiKey="AIzaSyBFZBQ1gPyJcp1NMDNs_1P22ThRMhYZdg4">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={currentLocation || clientLocation} // Використовує локацію клієнта як fallback
        zoom={13}
      >
        {currentLocation && (
          <DirectionsService
            options={{ 
              destination: clientLocation,
              origin: currentLocation,
              travelMode: 'DRIVING',
            }}
            callback={(response) => {
              if (response.status === 'OK') {
                setDirectionsResponse(response);
              } else {
                console.log('Directions request failed due to ', response.status);
              }
            }}
          />
        )}
        {directionsResponse && (
          <DirectionsRenderer options={{ directions: directionsResponse }} />
        )}
      </GoogleMap>
    </LoadScript>
  );
}

export default DriverMapPage;
