import React, { useState } from 'react';
import './RegisterPage.css';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [fullName, setFullName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [languages, setLanguages] = useState([]);
  const [message, setMessage] = useState('');

  const handleRegister = () => {
    const userData = { username, email, password, fullName, phoneNumber, languages };
    localStorage.setItem(email, JSON.stringify(userData)); // Use email as the key
    localStorage.setItem('currentUser', email); // Automatically login the user
    window.location.href = '/profile'; // Redirect to profile page
    setMessage("Registration successful. You are now logged in.");
  };

  return (
    <div className="register-page">
      <h2>Register</h2>
      {message && <div className="message">{message}</div>}
      <div className="input-container">
        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <select
          multiple={true}
          value={languages}
          onChange={(e) => setLanguages(Array.from(e.target.selectedOptions, option => option.value))}
        >
          {/* List of languages */}
        </select>
        <button onClick={handleRegister}>Register</button>
      </div>
    </div>
  );
}

export default RegisterPage;
