import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// Function to determine the driver's current location
function LocationMarker() {
  const [position, setPosition] = useState(null);
  const map = useMap();

  useEffect(() => {
    map.locate().on("locationfound", function (e) {
      setPosition(e.latlng);
      map.flyTo(e.latlng, map.getZoom());
    });
  }, [map]);

  return position === null ? null : (
    <Marker position={position}>
      <Popup>You are here</Popup>
    </Marker>
  );
}

function DriverMapPage() {
  return (
    <div className="driver-map-page">
      <h2>Driver Map</h2>
      <div className="video-and-description">
        <div className="video">
          {/* For demonstration, replace "videoURL" with your actual video link */}
          <video width="400" controls>
            <source src="videoURL" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="description">
          <p>This video explains how to effectively use the map to locate customers and navigate to their location.</p>
        </div>
      </div>
      <div className="map-container">
        <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false} style={{ height: 400, width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <LocationMarker />
          {/* Implement route drawing here */}
        </MapContainer>
      </div>
    </div>
  );
}

export default DriverMapPage;
