import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Custom hook to handle driver's current location
function useDriverLocation() {
  const [location, setLocation] = useState(null);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      () => {
        // Handle location error
        alert('Unable to retrieve your location');
      }
    );
  }, []);

  return location;
}

// Main DriverMapPage component
function DriverMapPage() {
  const driverLocation = useDriverLocation();

  return (
    <div className="driver-map-page">
      <h2>Driver Map</h2>
      <div style={{ display: 'flex', marginBottom: '20px' }}>
        <div>
          <video width="320" height="240" controls>
            <source src="path_to_your_video.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div style={{ marginLeft: '20px' }}>
          <p>Video Description: This video explains how to use the map to find your route and navigate to your client's location efficiently.</p>
        </div>
      </div>
      {driverLocation ? (
        <MapContainer center={driverLocation} zoom={13} style={{ height: '400px', width: '100%' }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={driverLocation}>
            <Popup>You are here</Popup>
          </Marker>
          {/* Add routing logic here */}
        </MapContainer>
      ) : (
        <p>Loading map...</p>
      )}
    </div>
  );
}

export default DriverMapPage;
