import React, { useState } from 'react';
import './MainPage.css';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [fullName, setFullName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [languages, setLanguages] = useState([]);

  const popularLanguages = ["English", "Spanish", "Mandarin", "French", "German"]; // Додайте до цього списку інші мови

  const handleRegister = () => {
    const userData = { username, email, password, fullName, phoneNumber, languages };
    localStorage.setItem(username, JSON.stringify(userData));
    alert("Registration successful. You can now log in.");
  };

  return (
    <div className="register-page">
      <h2>Register</h2>
      <div className="input-container">
        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <select multiple={true} value={languages} onChange={(e) => setLanguages(Array.from(e.target.selectedOptions, option => option.value))}>
          {popularLanguages.map((language) => (
            <option key={language} value={language}>{language}</option>
          ))}
        </select>
        <button onClick={handleRegister}>Register</button>
      </div>
    </div>
  );
}

export default RegisterPage;
