import React, { useState, useEffect } from 'react';
import './ProfilePage.css'; // Переконайтеся, що це правильний шлях до вашого CSS файлу

function ProfilePage() {
  const [userData, setUserData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    languages: [],
    rating: 0,
    photo: '',
  });

  const popularLanguages = ["English", "Spanish", "Mandarin", "French", "German", "Arabic", "Hindi", "Bengali", "Russian", "Portuguese", "Ukrainian"];

  useEffect(() => {
    const username = localStorage.getItem('currentUser');
    const storedUserData = JSON.parse(localStorage.getItem(username));
    if (storedUserData) {
      setUserData({ ...storedUserData });
    }
  }, []);

  const validateForm = () => {
    const nameRegex = /^[A-Z][a-z]+ [A-Z][a-z]+$/;
    const phoneRegex = /^\+[0-9]{9,15}$/;
    const emailRegex = /.*@.*/;

    if (!nameRegex.test(userData.fullName)) {
      alert("Full name must consist of first and last name, starting with capital letters, and only contain letters.");
      return false;
    }

    if (!phoneRegex.test(userData.phoneNumber)) {
      alert("Phone number must start with + and contain 10 to 16 digits.");
      return false;
    }

    if (!emailRegex.test(userData.email)) {
      alert("Email must contain '@'.");
      return false;
    }

    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "languages") {
      setUserData((prevData) => ({
        ...prevData,
        [name]: [...e.target.selectedOptions].map(option => option.value),
      }));
    } else {
      setUserData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  const handleSaveChanges = () => {
    if (validateForm()) {
      const username = localStorage.getItem('currentUser');
      localStorage.setItem(username, JSON.stringify(userData));
      alert('Profile updated successfully.');
    }
  };

  // Функції для обробки завантаження фото, виходу з акаунту та видалення акаунту...

  return (
    <div className="profile-page">
      <h2>User Profile</h2>
      <div className="profile-photo">
        {userData.photo ? <img src={userData.photo} alt="Profile" /> : <p>No photo uploaded</p>}
        <input type="file" onChange={handlePhotoChange} />
      </div>
      <div className="profile-info">
        {/* Поля для введення даних */}
        <select multiple={true} name="languages" value={userData.languages} onChange={handleChange} size={5}>
          {popularLanguages.map((language) => (
            <option key={language} value={language}>{language}</option>
          ))}
        </select>
        <button className="save-changes-btn" onClick={handleSaveChanges}>Save Changes</button>
      </div>
      <div className="profile-actions">
        {/* Кнопки для виходу з акаунту та видалення акаунту */}
      </div>
    </div>
  );
}

export default ProfilePage;
