import React, { useState, useEffect } from 'react';
import './ProfilePage.css';

function ProfilePage() {
  const [userData, setUserData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    languages: [],
    rating: 0,
    photo: 'image.jpeg',
  });

  useEffect(() => {
    const email = localStorage.getItem('currentUser');
    if (!email) {
      alert('Please log in to view this page.');
      window.location.href = '/login';
      return;
    }
    const userCredentials = localStorage.getItem(email);
    if (userCredentials) {
      const data = JSON.parse(userCredentials);
      setUserData(data);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "languages") {
      // Toggle language selection without the need for Ctrl or Command key
      const newLanguages = userData.languages.includes(value)
        ? userData.languages.filter(lang => lang !== value)
        : [...userData.languages, value];
      setUserData(prev => ({ ...prev, [name]: newLanguages }));
    } else {
      setUserData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSaveChanges = () => {
    const email = localStorage.getItem('currentUser');
    localStorage.setItem(email, JSON.stringify(userData));
    alert('Profile updated successfully.');
  };

  const handleDeleteAccount = () => {
    if (window.confirm("Are you sure you want to delete your account?")) {
      const email = localStorage.getItem('currentUser');
      localStorage.removeItem(email);
      localStorage.removeItem('currentUser');
      localStorage.removeItem('isLoggedIn');
      window.location.href = '/login';
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('currentUser');
    window.location.href = '/login';
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      setUserData(prev => ({ ...prev, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="profile-page">
      <h2>User Profile</h2>
      <div className="profile-photo">
        <img src={userData.photo || 'image.jpeg'} alt="Profile" />
        <input type="file" onChange={handlePhotoChange} />
      </div>
      <div className="profile-info">
        <input type="text" name="fullName" value={userData.fullName} onChange={handleChange} placeholder="Full Name" />
        <input type="text" name="phoneNumber" value={userData.phoneNumber} onChange={handleChange} placeholder="Phone Number" />
        <input type="email" name="email" value={userData.email} onChange={handleChange} placeholder="Email" />
        <select multiple name="languages" value={userData.languages} onChange={handleChange} className="multi-select">
          <option value="English">English</option>
          <option value="Spanish">Spanish</option>
          <option value="Mandarin">Mandarin</option>
          <option value="French">French</option>
          <option value="German">German</option>
          <option value="Arabic">Arabic</option>
          <option value="Hindi">Hindi</option>
          <option value="Bengali">Bengali</option>
          <option value="Russian">Russian</option>
          <option value="Portuguese">Portuguese</option>
          <option value="Ukrainian">Ukrainian</option>
        </select>
        <button className="save-changes-btn" onClick={handleSaveChanges}>Save Changes</button>
      </div>
      <div className="profile-actions">
        <button className="action-btn" onClick={handleDeleteAccount}>Delete Account</button>
        <button className="action-btn" onClick={handleLogout}>Log out</button>
      </div>
    </div>
  );
}

export default ProfilePage;
