import React, { useState, useEffect } from 'react';
import './ProfilePage.css';
function ProfilePage() {
  const [userData, setUserData] = useState({
    fullName: '',
    phoneNumber: '',
    email: '',
    languages: '',
    rating: 0,
    photo: '',
  });

  useEffect(() => {
    const username = localStorage.getItem('currentUser'); // Змініть це згідно з вашою логікою аутентифікації
    const storedUserData = JSON.parse(localStorage.getItem(username));
    if (storedUserData) {
      setUserData({ ...storedUserData });
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSaveChanges = () => {
    const username = localStorage.getItem('currentUser');
    localStorage.setItem(username, JSON.stringify(userData));
    alert('Profile updated successfully.');
  };

  const handleDeleteAccount = () => {
    const username = localStorage.getItem('currentUser');
    localStorage.removeItem(username); 
    localStorage.removeItem('isLoggedIn'); 
    window.location.href = '/';
  };

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    window.location.href = '/';
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setUserData((prevData) => ({
        ...prevData,
        photo: reader.result,
      }));
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="profile-page">
      <h2>User Profile</h2>
      <div className="profile-photo">
        {userData.photo ? <img src={userData.photo} alt="Profile" /> : <p>No photo uploaded</p>}
        <input type="file" onChange={handlePhotoChange} />
      </div>
      <div className="profile-info">
        <input
          type="text"
          name="fullName"
          value={userData.fullName}
          onChange={handleChange}
          placeholder="Full Name"
        />
        <input
          type="text"
          name="phoneNumber"
          value={userData.phoneNumber}
          onChange={handleChange}
          placeholder="Phone Number"
        />
        <input
          type="email"
          name="email"
          value={userData.email}
          onChange={handleChange}
          placeholder="Email"
        />
        <input
          type="text"
          name="languages"
          value={userData.languages}
          onChange={handleChange}
          placeholder="Languages"
        />
        <p><strong>Rating:</strong> {userData.rating} / 5</p>
        <button className="save-changes-btn" onClick={handleSaveChanges}>Save Changes</button>
      </div>
      <div className="profile-actions">
        <button className="logout-btn" onClick={handleLogout}>Log out</button>
        <button className="delete-account-btn" onClick={handleDeleteAccount}>Delete Account</button>
      </div>
    </div>
  );
}

export default ProfilePage;
