import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, DirectionsRenderer, DirectionsService } from '@react-google-maps/api';
import './DriverMapPage.css';

const containerStyle = {
  width: '100%',
  height: '100%',
};

const clientLocation = {
  lat: 49.8397,
  lng: 24.0297,
};

function DriverMapPage() {
  const [currentLocation, setCurrentLocation] = useState({
    lat: 49.8429,
    lng: 24.0263,
  });
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const mapRef = useRef();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setCurrentLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      () => {
        alert('Error: The Geolocation service failed.');
      }
    );
  }, []);

  const onMapLoad = (map) => {
    mapRef.current = map;
  };

  const onDragEnd = () => {
    const newCenter = mapRef.current.getCenter();
    setCurrentLocation({
      lat: newCenter.lat(),
      lng: newCenter.lng(),
    });
  };

  const fetchDirections = () => {
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: currentLocation,
        destination: clientLocation,
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirectionsResponse(result);
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );
  };

  useEffect(() => {
    if (currentLocation) {
      fetchDirections();
    }
  }, [currentLocation]);

  return (
    <div className="driver-map-page">
      <div className="driver-map-video-instructions">
        <div className="video-container">
          <video width="100%" controls>
            <source src="video.mp4" type="mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="instructions-container">
          <h4>How to Use the Map</h4>
          <p>Follow these instructions to learn how to use the map effectively to locate your position and navigate towards your destination.</p>
        </div>
      </div>
      <div className="map-container">
        <LoadScript googleMapsApiKey="AIzaSyBFZBQ1gPyJcp1NMDNs_1P22ThRMhYZdg4">
          <GoogleMap
            mapContainerStyle={containerStyle}
            center={currentLocation}
            zoom={13}
            onLoad={onMapLoad}
            onDragEnd={onDragEnd}
            options={{
              draggable: true,
              zoomControl: true,
            }}
          >
            {directionsResponse && (
              <DirectionsRenderer directions={directionsResponse} />
            )}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
}

export default DriverMapPage;
